import React from 'react';
import axios from 'axios';
import s from './styles.css';

const goeasy = "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3";
const dreams = "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-3.mp3";
const chillout ="https://www.soundhelix.com/examples/mp3/SoundHelix-Song-10.mp3";
const twistter ="https://www.soundhelix.com/examples/mp3/SoundHelix-Song-13.mp3";
const freebird = "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-15.mp3";

function getTime(time) {
  if (!isNaN(time)) {
    return (
      Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2)
    );
  }
}
  
  class MusicPlayer extends React.Component {
    _isMounted = false;

    constructor(props) {
       
        super(props);
        this._isMounted = false;
        this.state = { value: "",
                        currentSong: null,
                        music: "stopped",
                        currentTime: null,
                        duration: null
        };
      }


      componentWillReceiveProps(nextProps) {
        console.log('componentWillReceiveProps real song', nextProps.song); 
        console.log('componentWillReceiveProps value pass', this.props.song);  
        console.log('componentWillReceiveProps status music', this.props.music); 

        this.state.currentSong = nextProps.song;
        this.state.music = nextProps.music;
    }  

      componentDidUpdate(prevProps, prevState) {

        console.log('componentWillReceiveProps this.state.currentSong ', nextProps.song); 
        console.log('componentWillReceiveProps value pass', this.props.song);  
        console.log('componentWillReceiveProps status music', this.props.music); 


        if (this.state.currentSong !== prevState.currentSong) {
          let track;
          switch (this.state.currentSong) {
            case "Go Easy":
              track = goeasy;
              break;
            case "Dreams":
              track = dreams;
              break;
            case "Chill Out":
              track = chillout;
              break;
            case "Twistter":
              track = twistter;
              break;
            case "Free Bird":
              track = freebird;
              break;
            default:
              break;
          }
    
          if (track) {
            this.music.src = track;
            this.music.play();
            this.setState({
              music: "playing"
            });
          }
        }
    
        if (this.state.music !== prevState.music) {
          if (this.state.music === "paused") {
            this.music.pause();
          }
          if (this.state.music === "playing" && prevState.music === "paused") {
            this.music.play();
          }
          if (this.state.music === "stop") {
            this.music.pause();
            this.currentTime = 0;
            this.setState({
              currentSong: null
            });
          }
        }
      }
    
      componentWillUnmount() {
        this._isMounted = false;
        this.music.removeEventListener("timeupdate", () => {});
      }
      




       render() {
        const currentTime = getTime(this.state.currentTime);
        const duration = getTime(this.state.duration)
     
        return (
<>
        <h1 className="header"> Music Player</h1>
           
        <div className="App">
          <div className="ui card main-contai[ner">
            <div className="info-container"> {this.state.value}
            {this.state.currentSong}
            {this.state.music}
            </div>
          </div>
        </div>
      </>
    );
  }
}
export default MusicPlayer;
