import React from 'react';
import axios from 'axios';
import s from './styles.css';
import MusicPlayer from './MusicPlayer';


  class MusicList2 extends React.Component {
    state = {
        musicList: [],
        currentSong: null,
        music: "stopped"
      };
      

     

      componentDidMount() {
      axios.get(`https://avdeevaelena.github.io/json/music.json`)
      .then(res => {
        const musicList = res.data;
        this.setState({ musicList });
      }) 


      }
     
       render() {    
        const playList = this.state.musicList.map(item => {
          return (
            <div class="ui list">
              <div class="item">
                <img class="ui avatar image" src={item.url} alt="music images" />
                <div class="content">
                  <li
                    className="title"
                    key={item.id}
                    onClick={() => this.setState({ currentSong: item.title })}
                  >
                    {item.title}
                  </li>
                </div>
              </div>
              <audio ref={ref => (this.musicPla = ref)} />
            </div>
          );
        });
//  <MusicPlayer my={this.state.currentSong} />  

        return (
          <div>
            <div className="playlist">   {playList}</div>
            <MusicPlayer song={this.state.currentSong}
                         state={this.state.music}
            /> 

          </div>
        )

  }



  componentDidMount() {
    this.music.addEventListener("timeupdate", e => {
      this.setState({
        currentTime: e.target.currentTime,
        duration: e.target.duration
      });
    });
  }

  componentWillUnmount() {
    this.music.removeEventListener("timeupdate", () => {});
  }

}
export default MusicList2;
