import React from 'react';
import axios from 'axios';
import s from './styles.css';

const goeasy = "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3";
const dreams = "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-3.mp3";
const chillout ="https://www.soundhelix.com/examples/mp3/SoundHelix-Song-10.mp3";
const twistter ="https://www.soundhelix.com/examples/mp3/SoundHelix-Song-13.mp3";
const freebird = "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-15.mp3";

function getTime(time) {
  if (!isNaN(time)) {
    return (
      Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2)
    );
  }
}
  
  class MusicList extends React.Component {
    state = {
        musicList: [],
        currentSong: null,
        music: "stopped",
        currentTime: null,
        duration: null
      };

      componentDidUpdate(prevProps, prevState) {
        if (this.state.currentSong !== prevState.currentSong) {
          let track;
          switch (this.state.currentSong) {
            case "Go Easy":
              track = goeasy;
              break;
            case "Dreams":
              track = dreams;
              break;
            case "Chill Out":
              track = chillout;
              break;
            case "Twistter":
              track = twistter;
              break;
            case "Free Bird":
              track = freebird;
              break;
            default:
              break;
          }
    
          if (track) {
            this.music.src = track;
            this.music.play();
            this.setState({
              music: "playing"
            });
          }
        }
    
        if (this.state.music !== prevState.music) {
          if (this.state.music === "paused") {
            this.music.pause();
          }
          if (this.state.music === "playing" && prevState.music === "paused") {
            this.music.play();
          }
          if (this.state.music === "stop") {
            this.music.pause();
            this.currentTime = 0;
            this.setState({
              currentSong: null
            });
          }
        }
      }
      componentWillUnmount() {
        this.music.removeEventListener("timeupdate", () => {});
      }
      componentDidMount() {
     

      axios.get(`https://avdeevaelena.github.io/json/music.json`)
      .then(res => {
        const musicList = res.data;
        this.setState({ musicList });
      }) 
      }
     
       render() {
        const currentTime = getTime(this.state.currentTime);
        const duration = getTime(this.state.duration);
        const playlist = this.state.musicList.map(item => {
          return (
            <div class="ui list">
              <div class="item">
                <img class="ui avatar image" src={item.url} alt="music images" />
                <div class="content">
                  <li
                    className="title"
                    key={item.id}
                    onClick={() => this.setState({ currentSong: item.title })}
                  >
                    {item.title}
                  </li>
                </div>
              </div>
            </div>
          );
        });
        return (
<>
        <h1 className="header">React Music Player</h1>
        <ul>
            { this.state.musicList.map(song =><li>{song.title}</li>)}
          </ul>
        <div className="App">
          <div className="ui card main-container">
            <div className="info-container">
              {this.state.music === "playing" ? (
                <div className="current-song">
                  Now Playing {this.state.currentSong}
                </div>
              ) : null}
              {this.state.music === "paused" ? (
                <div className="current-song">
                  {this.state.currentSong} is paused{" "}
                </div>
              ) : null}
              {this.state.music === "playing" ||
              this.state.music === "paused" ? (
                <div>
                  {currentTime} / {duration}
                </div>
              ) : (
                ""
              )}
            </div>
            <div className="button-container">
              {this.state.music === "paused" && (
                <button
                  class="ui labeled icon red button"
                  onClick={() => this.setState({ music: "playing" })}
                >
                  <i class=" large play circle outline icon" />
                  Play
                </button>
              )}
              {this.state.music === "playing" && (
                <button
                  class="ui labeled icon red button"
                  onClick={() => this.setState({ music: "paused" })}
                >
                  <i class="large pause circle outline icon" />
                  Pause
                </button>
              )}
              {this.state.music === "playing" ||
              this.state.music === "paused" ? (
                <button
                  class="ui labeled icon button"
                  onClick={() => this.setState({ music: "stop" })}
                >
                  <i class="large stop circle outline icon" />
                  Stop
                </button>
              ) : null}
            </div>
            <div className="playlist">{playlist}</div>
            <audio ref={ref => (this.music = ref)} />
          </div>
        </div>
      </>
    );
  }
}
export default MusicList;
