const dreams = "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-3.mp3";
const chillout ="https://www.soundhelix.com/examples/mp3/SoundHelix-Song-10.mp3";
const twistter ="https://www.soundhelix.com/examples/mp3/SoundHelix-Song-13.mp3";
const freebird = "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-15.mp3";

function getTime(time) {
  if (!isNaN(time)) {
    return (
      Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2)
    );
  }
}
  
  class MusicPlayer extends React.Component {
    _isMounted = false;

    constructor(props) {
       
        super(props);
        this._isMounted = false;
        this.state = { value: "",
                        currentSong: null,
                        music: "stopped",
                        currentTime: null,
                        duration: null
        };
      }

   
    componentDidMount() {
        this._isMounted = true;
   
        this.music.addEventListener("timeupdate", e => {
          this.setState({
            currentTime: e.target.currentTime,
            duration: e.target.duration
          });
        });
      }
    
      componentWillUnmount() {
        this._isMounted = false;
        this.music.removeEventListener("timeupdate", () => {});
      }
      




       render() {
        const currentTime = getTime(this.state.currentTime);
