import React from 'react';
import axios from 'axios';
import s from './styles.css';

const goeasy = "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3";
const dreams = "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-3.mp3";
const chillout ="https://www.soundhelix.com/examples/mp3/SoundHelix-Song-10.mp3";
const twistter ="https://www.soundhelix.com/examples/mp3/SoundHelix-Song-13.mp3";
const freebird = "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-15.mp3";

function getTime(time) {
  if (!isNaN(time)) {
    return (
      Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2)
    );
  }
}
  
  class MusicList extends React.Component {
    state = {
        currentSong: null,
        music: "stopped",
        currentTime: null,
        duration: null
      };

    requestData(){
        const self = this;
        const data_url = 'https://avdeevaelena.github.io/json/music.json';
        const rowsTitle= [];
        const rowsId= [];
        const rowsImgUrl= [];
        axios.get(data_url)
                .then(function (response) {
                        const dataMusic=response.data;
                    for (let i = 0; i < dataMusic.length; i += 1) {
                        rowsId.push([dataMusic[i].id] );
                        rowsTitle.push([dataMusic[i].title] );
                        rowsImgUrl.push([dataMusic[i].url] );
             }
                    self.setState({musicId: rowsId});
                    self.setState({musicTitle: rowsTitle});
                    self.setState({musicUrl: rowsImgUrl[0]});
                })
                .catch(function (error) { 
                    console.log(error);
                })
                .finally(function () {     
                });     
    }


    componentDidUpdate(prevProps, prevState) {

        console.log (this.state.currentSong, "name song");
        console.log (this.state.currentSong =="Campfire Story", "DidUpdate");
        console.log (this.state.currentSong =="Chill Out", 'Chill Out');
        if (this.state.currentSong !== prevState.currentSong) {
          let track;
          switch (this.state.currentSong) {
            case "Campfire Story":
              track = goeasy;
              break;
            case "Dreams":
              track = dreams;
              break;
            case "Chill Out":
              track = chillout;
              break;
            case "Twistter":
              track = twistter;
              break;
            case "Free Bird":
              track = freebird;
              break;
            default:
              break;
          }
    
          if (track) {
            this.music.src = track;
            this.music.play();
            this.setState({
              music: "playing"
            });
          }
        }
    
        if (this.state.music !== prevState.music) {
          if (this.state.music === "paused") {
            this.music.pause();
          }
          if (this.state.music === "playing" && prevState.music === "paused") {
            this.music.play();
          }
          if (this.state.music === "stop") {
            this.music.pause();
            this.currentTime = 0;
            this.setState({
              currentSong: null
            });
          }
        }
      }
    
      componentDidMount() {

        this.music.addEventListener("timeupdate", e => {
          this.setState({
            currentTime: e.target.currentTime,
            duration: e.target.duration
          });
        });

        this.requestData();    


      }
    
      componentWillUnmount() {
        this.music.removeEventListener("timeupdate", () => {});
      }

    render() {
        const currentTime = getTime(this.state.currentTime);
        const duration = getTime(this.state.duration);
        const songTitle = this.state.musicTitle;
        const songId = this.state.musicId;
        const songImg = this.state.musicUrl;

        const playlist = [
          {
            id: 1,
            title: "Go Easy",
            url:
              "https://thumbs-prod.si-cdn.com/Vj7Cmc62xkQLwQZLiX1SbOV89ik=/420x240/https://public-media.si-cdn.com/filer/cd/0e/cd0efbec-bc15-4f38-894a-7e0e6f5968b8/campfire_edit.jpg"
          },
          {
            id: 2,
            title: "Dreams",
            url:
              "https://images.pexels.com/photos/96380/pexels-photo-96380.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
          },
          {
            id: 3,
            title: "Chill Out",
            url:
              "https://scontent.fbom11-1.fna.fbcdn.net/v/t1.0-9/54433190_2258561064201100_2218823514348060672_o.jpg?_nc_cat=107&_nc_ohc=14CJbrdmXusAQkPOoL9gbVg6VtyPIqSwXK7UiPTZ2llwMbExe7PuES1Ag&_nc_ht=scontent.fbom11-1.fna&oh=f5dde330f28bb39b984dcfb88cded3a2&oe=5EA792C6"
          },
          {
            id: 4,
            title: "Twistter",
            url: "https://i.ytimg.com/vi/VJ52yJwN_K0/maxresdefault.jpg"
          },
          {
            id: 5,
            title: "Free Bird",
            url:
              "https://www.laurenswilliam.nl/wp-content/uploads/2016/09/Chill-music-update-1.jpeg"
          }
        ].map(item => {
          return (
            <div class="ui list">
              <div class="item">
                <img class="ui avatar image" src={item.url} alt="music images" />
                <div class="content">
                  <li
                    className="title"
                    key={item.id}
                    onClick={() => this.setState({currentSong: item.title })}
                  >
                    {item.title}
                  </li>
                </div>
              </div>
            </div>
          );
        });
    
        return (
            <>
            
              <h1 className="header">React Music Player</h1>


              <div className="App">
                <div className="ui card main-container">
                  <div className="info-container">
                    {this.state.music === "playing" ? (
                      <div className="current-song">
                        Now Playing {this.state.currentSong}
                      </div>
                    ) : null}
                    {this.state.music === "paused" ? (
                      <div className="current-song">
                        {this.state.currentSong} is paused{" "}
                      </div>
                    ) : null}
                    {this.state.music === "playing" ||
                    this.state.music === "paused" ? (
                      <div>
                        {currentTime} / {duration}
                      </div>
                    ) : (
                      ""
                    )}
                  </div>
                  <div className="button-container">
                    {this.state.music === "paused" && (
                      <button
                        class="ui labeled icon red button"
                        onClick={() => this.setState({ music: "playing" })}
                      >
                        <i class=" large play circle outline icon" />
                        Play
                      </button>
                    )}
                    {this.state.music === "playing" && (
                      <button
                        class="ui labeled icon red button"
                        onClick={() => this.setState({ music: "paused" })}
                      >
                        <i class="large pause circle outline icon" />
                        Pause
                      </button>
                    )}
                    {this.state.music === "playing" ||
                    this.state.music === "paused" ? (
                      <button
                        class="ui labeled icon button"
                        onClick={() => this.setState({ music: "stop" })}
                      >
                        <i class="large stop circle outline icon" />
                        Stop
                      </button>
                    ) : null}
                  </div>
                  <div className="playlist"> playlist
                  <div class="ui list">
                  <div class="item">
                <img class="ui avatar image" src={songImg} alt="music images" />
                <div class="content">
                  <li
                    className="title"
                    key={songId}
                    onClick={() => this.setState({currentSong:songTitle[0][0]})}
                  >
                   {songTitle}
                  </li>
                </div>
                </div>
              </div>
              pg{songTitle}
              {this.state.currentSong}
            </div>
            <div className="playlist"> playlist
                  <div class="ui list">
                  <div class="item">
                <img class="ui avatar image" src={songImg} alt="music images" />
                <div class="content">
                  <li
                    className="title"
                    key={songId}
                    onClick={() => this.setState({currentSong:songTitle[1][0]})}
                  >
                   {songTitle}
                  </li>
                </div>
                </div>
              </div>
              pg{songTitle}
              {this.state.currentSong}
            </div>
            <audio ref={ref => (this.music = ref)} />
            this.state{this.state.currentSong}
          </div>
        </div>
      </>
            
          );
    }
  }
  
export default MusicList;
