import React from 'react';
import axios from 'axios';
import MusicPlayer from './MusicPlayer';
import { Card, Col, Row } from 'antd';
import s from './MusicList2.module.css';
const { Meta } = Card;

  class MusicList2 extends React.Component {
    state = {
        musicList: [],
        currentSong: null,
        music: "stopped"
      };
      componentDidMount() {
      axios.get(`https://avdeevaelena.github.io/json/music3.json`)
      .then(res => {
        const musicList = res.data;
        this.setState({ musicList });
      }) 
      }

      
       render() {    
        const playList = this.state.musicList.map(item => {
          return (
            <div class="ui list">
              <Row gutter={3}>
              <div class="item">
              <Col span={3}>
              <img className={s.mus_pic} src={item.url} />
                <div class="content">
                  <Card
                    className="title"
                    key={item.id}
                    onClick={() => this.setState({ currentSong: item.title })}
                     title={item.title} 
                    />    
                </div>
                </Col>
              </div>
              </Row>
            </div>
          );
        });

/*  <MusicPlayer song={this.state.currentSong}
                         state={this.state.music}
            /> */


        return (
          <div>
            <div className="playlist">   {playList}</div>
             <MusicPlayer song={this.state.currentSong}
                         status={this.state.music}
            /> 

          </div>
        )

  }



 

}
export default MusicList2;
